    public function handleUnknownCommand(Stringable $text): void
    {
        if ($text->value() != Telegraph::getRegisteredCommands()){
            $this->reply("–¢–∞–∫–æ—ó –∫–æ–º–∞–Ω–¥–∏ —è –Ω–µ –∑–Ω–∞—é.ü§î\n–°–∫–æ—Ä–∏—Å—Ç—É–π—Å—è –æ–¥–Ω—ñ—î—é –∑ —Ç–∏—Ö, —â–æ –¥–æ—Å—Ç—É–ø–Ω—ñ –∑–∞ –∫–æ–º–∞–Ω–¥–æ—é /help");
        }
    }
    public function dice(){
        $this->reply("–ó–∞—Ä–∞–∑ –∫–∏–Ω—É, –¥–∞–π –ø–æ—Ç—Ä—è—Å—Ç–∏!");
        sleep(2);
        $this->chat->dice()->send();
    }

    public function start(){
        $this->reply("–ü—Ä–∏–≤—ñ—Ç!üëã –Ø –±–æ—Ç <b>GUD_E</b>! \n\n–£ –º–µ–Ω–µ —â–µ –Ω–∞–±–∞–≥–∞—Ç–æ —Ñ—É–Ω–∫—Ü—ñ–π, –∞–ª–µ —è —Ä–æ–∑–≤–∏–≤–∞—é—Å—å. \n\n–ü–æ–∫–∏ —Ç–∏ –º–æ–∂–µ—à—å –ø–æ–¥–∏–≤–∏—Ç–∏—Å—è –≤—Å–µ, —â–æ –º–µ–Ω—ñ –≤—ñ–¥–æ–º–æ –∑–∞ –∫–æ–º–∞–Ω–¥–æ—é /help");
    }

    public function help(){
        $this->reply("–ü–æ–∫–∞–∑—É—é –≤—Å—ñ –∫–æ–º–∞–Ω–¥–∏, —è–∫—ñ –º–µ–Ω—ñ –Ω–∞—Ä–∞–∑—ñ –≤—ñ–¥–æ–º—ñ:\n\n /help - –ø–æ–∫–∞–∑—É—é —É—Å—ñ –¥–æ—Å—Ç—É–ø–Ω—ñ –∫–æ–º–∞–Ω–¥–∏ \n /site - –ø–æ–∫–∞–∑—É—é —É—Å—ñ –ø–æ—Å–∏–ª–∞–Ω–Ω—è –Ω–∞ —Å–∞–π—Ç \n /units - –ø–æ–∫–∞–∑—É—é —É—Å—ñ—Ö —é–Ω—ñ—Ç—ñ–≤ \n /fractions - –ø–æ–∫–∞–∑—É—é —É—Å—ñ —Ñ—Ä–∞–∫—Ü—ñ—ó \n /components - –ø–æ–∫–∞–∑—É—é —É—Å—ñ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∏ \n /dice - –∫–∏–¥–∞—é –∫—É–±–∏–∫");
    }

    public function site(){
        $this->chat->message('–û–±–µ—Ä—ñ—Ç—å –ø–æ—Å–∏–ª–∞–Ω–Ω—è –∑–∞ —è–∫–∏–º —Ç—Ä–µ–±–∞ –ø–µ—Ä–µ–π—Ç–∏')
            ->keyboard(Keyboard::make()->buttons([
            Button::make('–ü–µ—Ä–µ–π—Ç–∏ –Ω–∞ —Å–∞–π—Ç')->url('http://127.0.0.1:8000/ua')->width(1),
            Button::make('–ü–µ—Ä–µ–π—Ç–∏ –¥–æ –ø—Ä–∞–≤–∏–ª')->url('http://127.0.0.1:8000/ua/rules')->width(0.5),
            Button::make('–ü–µ—Ä–µ–π—Ç–∏ –¥–æ —Å–ø–∏—Å–∫—É —é–Ω—ñ—Ç—ñ–≤')->url('http://127.0.0.1:8000/ua/units')->width(0.5),
            Button::make('–ü–µ—Ä–µ–π—Ç–∏ –¥–æ —Å–ø–∏—Å–∫—É –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ñ–≤')->url('http://127.0.0.1:8000/ua/components')->width(0.5),
            Button::make('–ü–µ—Ä–µ–π—Ç–∏ –¥–æ —Å–ø–∏—Å–∫—É —Ñ—Ä–∞–∫—Ü—ñ–π')->url('http://127.0.0.1:8000/ua/fractions')->width(0.5),
        ]))->send();
