                Button::make('–°–∫–∞—É—Ç')->action('scout'),
                Button::make('–°–Ω–∞–π–ø–µ—Ä')->action('sniper'),
                Button::make('–ö—É–ª–µ–º–µ—Ç–Ω–∏–∫')->action('machinegunner'),
                Button::make('–ü—ñ–ª–æ—Ç')->action('pilot'),
                Button::make('–ö–æ–º–∞–Ω–¥–∏—Ä –∞—Ä—Ç–∏–ª–µ—Ä—ñ—ó')->action('artycommander'),
            ]))->send();
    }
    public function rifleman(){
        $this->chat->message("<i>–°—Ç—Ä—ñ–ª–µ—Ü—å</i> –≤–æ–ª–æ–¥—ñ—î —Ç—ñ–ª—å–∫–∏ –≥–≤–∏–Ω—Ç—ñ–≤–∫–æ—é —ñ –Ω–æ–∂–µ–º. –ú–∞—î 10 –æ–¥. –∑–¥–æ—Ä–æ–≤'—è —ñ –±—Ä–æ–Ω—ñ. –ë–æ—î–∑–∞–ø–∞—Å —Ä–æ–∑—Ä–∞—Ö–æ–≤–∞–Ω–∏–π –Ω–∞ 10 –ø–æ—Å—Ç—Ä—ñ–ª—ñ–≤, —Å—Ç—Ä—ñ–ª—è—î –æ–¥–∏–Ω–∏—á–Ω–æ. –î–∞–ª—å–Ω—ñ—Å—Ç—å –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–≥–æ –ø–æ—Å—Ç—Ä—ñ–ª—É 8 –≥–µ–∫—Å—ñ–≤.")
        ->send();
